import './App.css'
import {BrowserRouter,Routes,Route} from 'react-router-dom'
import HomePage from './compo/HomePage';
import AddRoom from './compo/AddRoom';
import SelectedRoom from './compo/SelectedRoom';
import { useState } from 'react';



function App() {


  const [rooms,setRooms]=useState([])

const [selectedRoom,setSelectedRoom]=useState()
const [product, setProduct] = useState([])
// const [productRooms,setProductRooms]=useState([])

   const sendRoom=(index)=>{
    setSelectedRoom(rooms[index])
  }
  let mode; 

  const setColor=(i)=>{
    let mode=selectedRoom.product[i].mode
    if(mode==='red'){
     mode==='green'
     // Products.mode='red';
   }  
   else{
     mode==='red'
     // Products.mode=bgColor;
     // Products.mode='green'
   }  

  }





  // const addProductRoom=()=>{
  //  let productRoom= new ProductsRoom
  //  setProductRooms(...productRooms,productRoom)

  // }

  return (
    <div className="App">
       <BrowserRouter>
        <Routes>
          <Route path='/' element={<HomePage rooms={rooms} send={sendRoom}/>} />
          <Route path='/addroom' element={<AddRoom  rooms={rooms}/>} />
          <Route path='/room' element={<SelectedRoom selectedRoom={selectedRoom} rooms={setRooms} product={product} setProduct={setProduct} setColor={setColor} />}/>
        </Routes>


      </BrowserRouter>



    </div>
  );
}



export default App;
