import React,{useState} from 'react'



export default function Product(props) {
  const[bgColor,setBgColor]=useState('red')
  const[mode,setMode]=useState(false)
  
  
  const setColor=()=>{
    setMode(!mode) 
       if(mode===false){
     setBgColor('red')
     // Products.mode='red';
   }  
   else{
     setBgColor('green')
     // Products.mode=bgColor;
     // Products.mode='green'
   }  
   console.log(props.mode);
  }
    


  return (
    <div>
      <div ><button style={{backgroundColor:props.mode}} onClick={setColor} className='product'>{props.name}</button > <button onClick={()=>{props.delete(props.index)}}>x</button> </div>
    </div>
  )
}
