import React,{useState} from 'react'
import Product from './Product'

export default function SelectedRoom(props) {
  
  const[flag,setFlag]=useState(false)
  const[select2,setSelect2]=useState()
  const[product,setProduct]=useState([])

  const addProduct=()=>{
    debugger
    if(select2<1){
    alert('error')
    }
    else{
    let temp = {
      name: select2
      }
   setProduct([...product,temp])
    }
  }

  const showDiv=()=>{
    if(flag===true){
      return <div className='bar'>
<div>
<select  onChange={(e)=>{setSelect2(e.target.value)}} name="new product" className='select2' >
        <option value="title" disabled selected hidden>Choose new product</option>
        <option value="Air-conditioner">Air-conditioner</option>
        <option value="Boiler">Boiler</option>
        <option value="Stereo-sound">Stereo sound</option>
      </select>
      </div>  
      <div><button className='btn3' onClick={(val)=>{addProduct()}}>add</button></div>
      </div>
    }
    
  }

  return (
    <div className='mainDiv'>
      <div> <h2 className='title'>Smart house</h2> </div>
   
    {props.rooms.map((val)=>{
        return <div className='roomDiv2' style={{backgroundColor:val.color}}>  
       <div><p> <span className='subStitle'> Room name: </span>{val.name}  </p>
         <p> <span className='subStitle'> Room type </span>{val.select}</p> </div>
         <div><Product productArr={product}/></div>
         </div> 
    })
}



<button onClick={()=>{setFlag(!flag)}} className='btn2' >Add product</button>
{showDiv()}
    </div>
  )
}
