import React,{useState} from 'react'



export default function Product(props) {

    const [bgColor,setBgColor]=useState('red')
    
    const setColor=()=>{

         if(bgColor==='green'){
            setBgColor('red');
         }  
         else{
            setBgColor('red')
         }  
        }
    

  return (
    <div>
      <div style={{backgroundColor:bgColor}} onClick={()=>{setBgColor(!bgColor)}}><button className='product'>{props.name}</button> <button onClick={()=>{props.delete(props.index)}}>x</button> </div>
    </div>
  )
}
