import React from 'react'

export default function Room() {
  return (
    <div>
         <div className='roomDiv'> {props.rooms.map((val) => { 
        return <button className='room' style={{ backgroundColor: val.color }}> <p className='pRoom'>{val.name}</p> </button>
      })}
      </div>
    </div>
  )
}
