import React,{useState} from 'react'
import { Link } from 'react-router-dom'
import HomePage from './HomePage'



export default function AddRoom(props) {
 
    const[roomName,setRoomName]=useState('')
    const[roomColor,setRoomColor]=useState('')
  
    
    const checkName=()=>{
        if(roomName.length<1){
          alert('erorr')
        }
        else{
          let temp={
            name: roomName,
            color: roomColor
            }
            props.rooms.p(temp)
            }

              
        }
    
    
    return (
    <div className='mainDiv'>
    <h2 className='title'>Smart house</h2>
    <select name="new room" className='select' >
  <option value="title" disabled selected hidden>choose new room</option>
  <option value="badroom">Bedroom</option>
  <option value="toilet">Toilet</option>
  <option value="kitchen">Kitchen</option>
</select>
<br />

<input onChange={(e)=>{setRoomName(e.target.value)}} className='inp' type="text" placeholder='room name' /> <br />
<input onChange={(e)=>{setRoomColor(e.target.value)}} className='inp' type="text" placeholder='room color' /> <br />

<Link to={'/'}> <button onClick={()=>{checkName()}} className='btn1'>create</button> </Link>
    </div>
    
  )
}
