export default function SelectedRoom(props) {
  
  const[flag,setFlag]=useState(false)

  const showDiv=()=>{
    if(flag===true){
      return <div>

<select onChange={(e)=>{setSelect(e.target.value)}} name="new room" className='select' >
        <option value="title" disabled selected hidden>choose new room</option>
        <option value="badroom">Bedroom</option>
        <option value="toilet">Toilet</option>
        <option value="kitchen">Kitchen</option>
      </select>

      </div>
    }
  }

  return (
    <div className='mainDiv'>
      <div> <h2 className='title'>Smart house</h2> </div>
   
    {props.rooms.map((val)=>{
        return <div className='roomDiv2' style={{backgroundColor:val.color}}>  <p> <span className='subStitle'> Room name: </span>{val.name}  </p> <p> <span className='subStitle'> Room type: </span>{val.select}</p> </div>
    })
}



<button className='btn2' >Add product</button>
    </div>
  )
}
