import React, { useState } from 'react'
import Product from './Product'
import { Link } from 'react-router-dom'

export default function SelectedRoom(props) {

  const [flag, setFlag] = useState(false)
  const [select2, setSelect2] = useState('')
  const [productArr,setProductArr]=useState(props.selectedRoom.product)
  const[bgColor,setBgColor]=useState('red')

  class Products{
    name=''
    mode=false
    constructor(name){
      this.name=name
    }
  }

  const addProduct = () => {
    if (productArr.length > 4 || select2 < 1) {
      alert('error')
    }
    else {
     let temp=new Products(select2)
     setProductArr([...productArr,temp])  
    }
     
    }
   
  

  const showDiv = () => {
    if (flag === true) {
      return <div className='bar'>
        <div>
          <select onChange={(e) => { setSelect2(e.target.value) }} name="new product" className='select2' >
            <option value="title" disabled selected hidden>Choose new product</option>
            <option value="Air-conditioner">Air-conditioner</option>
            <option value="Boiler">Boiler</option>
            <option value="Stereo-sound">Stereo sound</option>
          </select>
        </div>
        <div><button className='btn3' onClick={(val) => { addProduct() }}>add</button></div>
      </div>
    }

  
  }

  const deleteProduct=(index)=>{
    let temp=[...productArr]
    temp.splice(index,1)
    setProductArr([...temp]);
    props.selectedRoom.product=productArr 
  }

  const setColor=()=>{
      
    if(Products.bgColor===false){
       P;
    }  
    else{
       setBgColor('green')
    }  
   }

  return (
    <div className='mainDiv'>
      <Link to={'/'}> <button onClick={()=>{props.selectedRoom.product=productArr }} style={{ float: 'left' }} >x</button> </Link><div> <h2 className='title'>Smart house</h2> </div>
      <div >
       
         <div className='roomDiv2' style={{ backgroundColor: props.selectedRoom.bgColor }}>
            <p> <span className='subStitle'> Room name: </span>{props.selectedRoom.name}</p>
            <p> <span className='subStitle'> Room type: </span>{props.selectedRoom.select}</p>
          </div>
      
        <div className='roomDiv3'> { productArr.map((val, i) => {
          return   <Product name={val.name} index={i} delete={deleteProduct} changeBgColor={setColor} bgColor={bgColor} />
        })
        }
        </div>
      </div>

      <button onClick={() => { setFlag(!flag) }} className='btn2' >Add product</button>
      {showDiv()}



    </div>




  )



}
