import React,{useState} from 'react'

export default function SelectedRoom(props) {
  
  const[flag,setFlag]=useState(false)
  const[select2,setSelect2]=useState()

  const showDiv=()=>{
    if(flag===true){
      return <div className='bar'>
<div>
<select onChange={(e)=>{setSelect2(e.target.value)}} name="new product" className='select' >
        <option value="title" disabled selected hidden>Choose new product</option>
        <option value="badroom">Air-conditioner</option>
        <option value="toilet">Boiler</option>
        <option value="kitchen">Stereo sound</option>
      </select>
      </div>
      <div><button>add</button></div>
      </div>
    }
    
  }

  return (
    <div className='mainDiv'>
      <div> <h2 className='title'>Smart house</h2> </div>
   
    {props.rooms.map((val)=>{
        return <div className='roomDiv2' style={{backgroundColor:val.color}}>  <p> <span className='subStitle'> Room name: </span>{val.name}  </p> <p> <span className='subStitle'> Room type: </span>{val.select}</p> </div>
    })
}



<button onClick={()=>{setFlag(!flag)}} className='btn2' >Add product</button>
{showDiv()}
    </div>
  )
}
