import React from 'react'

export default function SelectedRoom(props) {
  return (
    <div className='mainDiv'>
      <div> <h2 className='title'>Smart house</h2> </div>
   
    {props.rooms.map((val)=>{
        return <div className='roomDiv2' style={{backgroundColor:val.color}}>  <p> <span className='subStitle'> Room name: </span>{val.name}  </p> <p> <span className='subStitle'> Room type: </span>{val.select}</p> </div>
    })
}

<button>add product</button>
    </div>
  )
}
