import './App.css'
import {BrowserRouter,Routes,Route} from 'react-router-dom'
import HomePage from './compo/HomePage';
import AddRoom from './compo/AddRoom';
import SelectedRoom from './compo/SelectedRoom';
import { useState } from 'react';



function App() {


  const [rooms,setRooms]=useState([])

const [selectedRoom,setSelectedRoom]=useState([])
const [product, setProduct] = useState([])
const [productRooms,setSelectedRoomS]=useState([])

   const sendRoom=(index)=>{
    setSelectedRoom([rooms[index]])
  }

  const deleteProduct=(index)=>{
    let temp=[...product]
    temp.splice(index,1)
    setProduct([...temp]);
  }

  const showProduct=()=>{
   let productRoom= new ProductsRoom()
    setProduct(...productRooms,productRoom)

  }

  return (
    <div className="App">
       <BrowserRouter>
        <Routes>
          <Route path='/' element={<HomePage rooms={rooms} send={sendRoom}/>} />
          <Route path='/addroom' element={<AddRoom  rooms={rooms}/>} />
          <Route path='/room' element={<SelectedRoom selectedRoom={selectedRoom} rooms={setRooms} product={product} setProduct={setProduct} delete={deleteProduct} />}/>
        </Routes>


      </BrowserRouter>



    </div>
  );
}

class ProductsRoom{
  product= []

}


export default App;
