import React, { useState } from 'react'
import Product from './Product'
import { Link } from 'react-router-dom'

export default function SelectedRoom(props) {

  const [flag, setFlag] = useState(false)
  const [select2, setSelect2] = useState('')
  let roomname;

  
  class ProductsRoom{
    name=''
    product= []
    
    constructor(name,product){
     this.product=product;
      this.name=name;
    }

  }

  const addProduct = () => {
    if (props.product.length > 4 || select2 < 1) {
      alert('error')
    }
    else {
      let temp = {
        name: select2
      }
      let products=props.product
      props.setProduct([products, temp])
      new ProductsRoom(roomname,props.product)
    }
  }

  const showDiv = () => {
    if (flag === true) {
      return <div className='bar'>
        <div>
          <select onChange={(e) => { setSelect2(e.target.value) }} name="new product" className='select2' >
            <option value="title" disabled selected hidden>Choose new product</option>
            <option value="Air-conditioner">Air-conditioner</option>
            <option value="Boiler">Boiler</option>
            <option value="Stereo-sound">Stereo sound</option>
          </select>
        </div>
        <div><button className='btn3' onClick={(val) => { addProduct() }}>add</button></div>
      </div>
    }


    
  }


  return (
    <div className='mainDiv'>
      <Link to={'/'}> <button style={{ float: 'left' }} >x</button> </Link><div> <h2 className='title'>Smart house</h2> </div>
      <div >
        {props.selectedRoom.map((val) => {
          return roomname=val.name,<div className='roomDiv2' style={{ backgroundColor: val.color }}>
            <p> <span className='subStitle'> Room name: </span>{val.name}  </p>
            <p> <span className='subStitle'> Room type: </span>{val.select}</p>
          </div>
        })}
        <div className='roomDiv3'> {props.product.map((val, i) => {
          return   <Product name={val.name} index={i} delete={props.delete} />
        })
        }
        </div>
      </div>

      <button onClick={() => { setFlag(!flag) }} className='btn2' >Add product</button>
      {showDiv()}



    </div>




  )



}
